import * as React from 'react';
import { useState } from "react";
import axios from "axios";
import { AiOutlineEdit,AiFillPushpin,AiOutlinePushpin, AiOutlineLoading3Quarters } from "react-icons/ai";
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';

const url = "http://127.0.0.1:8000/api";

const NoteList = ({data, loading}) => {

    const [open, setOpen] = React.useState(false);
    const [title, setTitle] = useState('');
    const [id, setId] = useState(0);
    const [description, setDescription] = useState('');
    const [pin, setPin] = useState(false);
    const [tagline, setTagline] = useState('');
    const [datas, setDatas] = useState([]);

    const handleClickOpen = (id) => {
        console.log(id);
        setOpen(true);

        axios.get(`${url}/${id}/`)
        .then((response) => {
            setDatas(response.data);
        })
        .catch((error) => console.log(error));
      };
    
      const handleClose = () => {
        setOpen(false);
      };

    if(loading){
        return (
            <div className="loading">
                <h3 style={{textAlign: "center"}}> <span> <AiOutlineLoading3Quarters /> </span></h3>
            </div>
        )
    }
    


    return ( 
        <div className="row">
            <Grid container display="flex" justifycontent="center" spacing={{ xs: 2, md: 2 }} columns={{ xs: 4, sm: 8, md: 12 }}>
            {
                        data.map((note) => (
                            <Grid  justifycontent="center" item xs={2} sm={4} md={4} style={{ mx: "auto"}}>
                                <div key={note.id} className="notes" item xs={2} sm={4} md={4}>
                                    <Card spacing={2} direction="row" justifycontent="center" alignitems="flex-start"  sx={{ minWidth: 50, maxWidth: 350}} style={{ mx: "auto"}} variant="outlined">
                                        { (note.pin === true) ? <IconButton size="large" style={{display: 'flex', float: 'right',transform: "rotate(35deg)"}}><AiFillPushpin/></IconButton>: <Typography></Typography>}
                                        <CardContent>
                                            {/* { (note.updated_at !== "") ? <Typography sx={{ fontSize: 12 }} color="text.secondary" gutterBottom>
                                            Edited At : {note.updated_at}
                                            </Typography> :<Typography sx={{ fontSize: 12 }} color="text.secondary" gutterBottom>
                                            Created At : {note.created_at}
                                            </Typography> } */}
                                            <Typography variant="h5" component="div" noWrap={true}>
                                            {note.title}
                                            </Typography>
                                            <Typography sx={{ fontSize: 10 }} component="div" noWrap={true}>
                                            #{note.tagline}
                                            </Typography>
                                            <Typography variant="body2" noWrap={true}>
                                            {note.description}
                                            <br />
                                            </Typography>
                                        </CardContent>
                                        <CardActions>
                                            <Button size="large" onClick={() => { handleClickOpen(note.id)}}> <AiOutlineEdit style={{fontSize:20, paddingRight:10}} /> Edit </Button>
                                        </CardActions>
                                    </Card>
                                </div>
                            </Grid>
                        ))
                }
            </Grid>


            {/* POP UP FOR Note */}
            <Dialog open={open} onClose={handleClose} className="form">
            <DialogTitle>Add Note 
                    {/* <IconButton style={{ display: 'inline-block', alignItems: 'right'}}>
                    
                    
                    {pin? <AiFillPushpin onClick={()=>{
                        setPin(!pin)}}/>:
                        <AiOutlinePushpin onClick={()=>{
                        setPin(!pin)}} />
                    } */}
                    {/* <IconButton size="large" onClick={e => {
                    setPin(!pin)
                    }} style={{transform: "rotate(35deg)"}}>
                        {pin ?<AiFillPushpin/> : <AiOutlinePushpin/>}
                    </IconButton> */}
                        {/* <AiOutlinePushpin onClick={(e) => {setPin(true)}} title="Pin Note" /> */}
                    {/* </IconButton> */}
                </DialogTitle>
                
                <DialogContent>
                <TextField
                    margin="dense"
                    id="name"
                    type="text"
                    fullWidth
                    variant="standard"
                    placeholder="Note Title"
                    value={title}
                    // onChange={(e) => setTitle(e.target.value)}
                />
                <TextField
                    multiline
                    margin="dense"
                    type="text"
                    fullWidth
                    variant="standard"
                    placeholder="Tagline"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
                <TextField
                    margin="dense"
                    type="text"
                    fullWidth
                    variant="standard"
                    placeholder="Add you Note description here ..."
                    value={tagline}
                    onChange={(e) => setTagline(e.target.value)}
                />
                </DialogContent>
                <DialogActions>
                <Button onClick={handleClose}>Cancel</Button>
                <Button onClick={handleClose}>Save</Button>
                </DialogActions>
            </Dialog>


        </div>
     );
}
 
export default NoteList;
