import React from "react";
import { AiOutlinePushpin } from "react-icons/ai";
import { BiAddToQueue } from "react-icons/bi";
import logo from "../logo.png";
import { useState, useEffect } from "react";
import axios from "axios";
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import IconButton from '@mui/material/IconButton';


const url = "http://127.0.0.1:8000/api/list/";


const Header = () => {
    const [open, setOpen] = React.useState(false);
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [pin, setPin] = useState(false);

    const handleClickOpen = () => {
        setOpen(true);
      };
    
      const handleClose = () => {
        setOpen(false);
      };

      useEffect(() => {
        const getData = async () => {
                axios({
                    method: 'GET',
                    url: url
                })
                    .then((response) => {
                        setData(response.data);
                        console.log(response);
                    })
                    .catch((error) => console.log(error));
        }
        getData();
    },[]);


    return ( 
        <div>
            <nav className="header">
                    <h2><img src={logo} alt="logo" /><h2>NoteKeeper</h2></h2>
                    <div className="right"><button onClick={handleClickOpen} ><BiAddToQueue title="Add notes" /></button></div>
            </nav>

            <Dialog open={open} onClose={handleClose} className="form">
                <DialogTitle>Add Note 
                    <IconButton  style={{ display: 'inline-block', alignItems: 'right'}}>
                        <AiOutlinePushpin title="Pin Note" />
                    </IconButton>
                </DialogTitle>
                
                <DialogContent>
                <TextField
                    margin="dense"
                    id="name"
                    type="text"
                    fullWidth
                    variant="standard"
                    placeholder="Note Title"
                />
                <TextField
                    multiline
                    margin="dense"
                    type="text"
                    fullWidth
                    variant="standard"
                    placeholder="Add you Note description here ..."
                />
                </DialogContent>
                <DialogActions>
                <Button onClick={handleClose}>Cancel</Button>
                <Button onClick={handleClose}>Save</Button>
                </DialogActions>
            </Dialog>

        </div>
     );
}
 
export default Header;
