import * as React from 'react';
import { AiOutlineEdit, AiOutlineLoading3Quarters } from "react-icons/ai";
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';

const NoteList = ({data, loading}) => {
    console.log("Inside Note List");
    console.table(data);
    if(loading){
        return (
            <div className="loading">
                <h3 style={{textAlign: "center"}}> <span> <AiOutlineLoading3Quarters /> </span></h3>
            </div>
        )
    }

    return ( 
        <div className="row">
            <Grid container sx={{ my: 0 }} spacing={{ xs: 2, md: 3 }} columns={{ xs: 4, sm: 8, md: 12 }}>
            {
                        data.map((note) => (
                            <Grid item xs={2} sm={4} md={4}>
                                <div key={note.id} className="notes" item xs={2} sm={4} md={4}>
                                    <Card spacing={2} direction="row" justifyContent="center" alignItems="flex-start"  sx={{ minWidth: 50, maxWidth: 350}} style={{padding:30}} variant="outlined">
                                        <CardContent>
                                            <Typography sx={{ fontSize: 12 }} color="text.secondary" gutterBottom>
                                            {note.time}
                                            </Typography>
                                            <Typography variant="h5" component="div">
                                            {note.title}
                                            </Typography>
                                            <Typography variant="body2" noWrap="true">
                                            {note.description}
                                            <br />
                                            </Typography>
                                        </CardContent>
                                        <CardActions>
                                            <Button size="large"> <AiOutlineEdit style={{fontSize:20, paddingRight:10}} /> Edit </Button>
                                        </CardActions>
                                    </Card>
                                </div>
                            </Grid>
                        ))
                }
            </Grid>
        </div>
     );
}
 
export default NoteList;
