import { useState, useEffect } from "react";
import axios from "axios";
import './Home.scss';
import * as React from 'react';
import { AiOutlineEdit } from "react-icons/ai";
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

const url = "http://127.0.0.1:8000/api/list/";

const Home = () => {

    const [data, setData] = useState([]);
    
    useEffect(() => {
        const getData = async () => {
                axios({
                    method: 'GET',
                    url: url
                })
                    .then((response) => {
                        setData(response.data);
                        console.log(response);
                    })
                    .catch((error) => console.log(error));
        }
        getData();
    },[]);


    return ( 
        <div className="row">
            {
                    // data.map((note) => ( 
                        // <div className="card">
                        //     <div key={note.id} className="content">
                        //         <h3 id="copy" > <span> Title  </span><span1>: </span1>"{note.title}"</h3>
                        //         <p id="copy" > <span> DES </span><span1>: </span1>"{note.description}"</p>
                        //         <p id="copy" > <span>  Pin </span><span1>: </span1>"{note.pin}"</p>
                        //     </div>
                        // </div>
                        data.map((note) => (
                            <div key={note.id} style={{padding:30}} className="note">
                                <Card sx={{ minWidth: 50, maxWidth: 350}} style={{padding:30}} variant="outlined">
                                    <CardContent>
                                        <Typography sx={{ fontSize: 12 }} color="text.secondary" gutterBottom>
                                        Date and Time
                                        </Typography>
                                        <Typography variant="h5" component="div">
                                        {note.title}
                                        </Typography>
                                        <Typography variant="body2">
                                        {note.description}
                                        <br />
                                        </Typography>
                                    </CardContent>
                                    <CardActions>
                                        <Button size="small" ><AiOutlineEdit style={{fontSize:20}}  /> Edit</Button>
                                    </CardActions>
                                </Card>
                            </div>
                        ))
                        
                        
                    // ))
                }
        </div>
     );
}
 
export default Home;
