import * as React from 'react';
import { AiOutlineEdit, AiOutlineLoading3Quarters } from "react-icons/ai";
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

const NoteList = ({data, loading}) => {
    console.log("Inside Note List");
    console.table(data);
    if(loading){
        return (
            <div className="loading">
                <h3 style={{textAlign: "center"}}> <span> <AiOutlineLoading3Quarters /> </span></h3>
            </div>
        )
    }

    return ( 
        <div className="row">
            {
                        data.map((note) => (
                            <div key={note.id} style={{padding:30}} className="notes" >
                                <Card spacing={2} direction="row" justify="flex-start" alignItems="flex-start"  sx={{ minWidth: 50, maxWidth: 350}} style={{padding:30}} variant="outlined">
                                    <CardContent>
                                        <Typography sx={{ fontSize: 12 }} color="text.secondary" gutterBottom>
                                        {note.time}
                                        </Typography>
                                        <Typography variant="h5" component="div">
                                        {note.title}
                                        </Typography>
                                        <Typography variant="body2" noWrap="true">
                                        {note.description}
                                        <br />
                                        </Typography>
                                    </CardContent>
                                    <CardActions>
                                        <Button size="large"> <AiOutlineEdit style={{fontSize:20, paddingRight:10}} /> Edit </Button>
                                    </CardActions>
                                </Card>
                            </div>
                        ))
                }
        </div>
     );
}
 
export default NoteList;
