import { useState, useEffect } from "react";
import axios from "axios";
import './Home.scss';
import * as React from 'react';
import { AiOutlineEdit } from "react-icons/ai";
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

const url = "http://127.0.0.1:8000/api/list/";

const Home = () => {

    const [data, setData] = useState([]);
    
    useEffect(() => {
        const getData = async () => {
                axios({
                    method: 'GET',
                    url: url
                })
                    .then((response) => {
                        setData(response.data);
                        if(data.description.length > 30)
                        {
                            data.description = data.description[10];
                        }
                        console.log(response);
                    })
                    .catch((error) => console.log(error));
        }
        getData();
    },[]);


    return ( 
        <div className="row">
            {
                        data.map((note) => (
                            <div key={note.id} style={{padding:30}} className="notes" >
                                <Card spacing={2} direction="row" justify="flex-start" alignItems="flex-start"  sx={{ minWidth: 50, maxWidth: 350}} style={{padding:30}} variant="outlined">
                                    <CardContent>
                                        <Typography sx={{ fontSize: 12 }} color="text.secondary" gutterBottom>
                                        {note.time}
                                        </Typography>
                                        <Typography variant="h5" component="div">
                                        {note.title}
                                        </Typography>
                                        <Typography variant="body2" >
                                        {note.description}
                                        <br />
                                        </Typography>
                                    </CardContent>
                                    <CardActions>
                                        <Button size="large"> <AiOutlineEdit style={{fontSize:20, paddingRight:10}} /> Edit </Button>
                                    </CardActions>
                                </Card>
                            </div>
                        ))
                        
                        
                    // ))
                }
        </div>
     );
}
 
export default Home;
